import { useEffect, useState } from "react";
import "./Plans.css";
import { getAdminPlans, createPlan, deletePlan } from "../../services/planService";

const Plans = () => {
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(false);
  const [form, setForm] = useState({
    name: "",
    price: "",
    durationInDays: "",
    description: "",
  });

  const loadPlans = async () => {
    try {
      setLoading(true);
      const res = await getAdminPlans();
      setPlans(res.data.data || []);
    } catch (err) {
      alert(err.response?.data?.message || "Failed to load plans");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPlans();
  }, []);

  const handleChange = (e) => {
    setForm((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.name || !form.price || !form.durationInDays) {
      return alert("Name, Price, and Duration are required");
    }

    try {
      await createPlan({
        ...form,
        price: Number(form.price),
        durationInDays: Number(form.durationInDays),
      });
      setForm({ name: "", price: "", durationInDays: "", description: "" });
      loadPlans();
    } catch (err) {
      alert(err.response?.data?.message || "Failed to create plan");
    }
  };

  const handleDelete = async (id) => {
    const ok = window.confirm("Are you sure you want to delete this plan?");
    if (!ok) return;

    try {
      await deletePlan(id);
      loadPlans();
    } catch (err) {
      alert(err.response?.data?.message || "Failed to delete plan");
    }
  };

  return (
    <div className="plans-page">
      <h2>Membership Plans</h2>
      <p className="plans-subtitle">
        Manage the pricing and duration of memberships offered at FitQuest.
      </p>

      <div className="plans-layout">
        {/* Add Plan Form */}
        <div className="plans-form-card">
          <h3>Add New Plan</h3>
          <form onSubmit={handleSubmit} className="plans-form">
            <input
              name="name"
              placeholder="Plan Name (Monthly, Quarterly)"
              value={form.name}
              onChange={handleChange}
            />
            <input
              name="price"
              type="number"
              placeholder="Price (₹)"
              value={form.price}
              onChange={handleChange}
            />
            <input
              name="durationInDays"
              type="number"
              placeholder="Duration in Days (e.g. 30, 90, 365)"
              value={form.durationInDays}
              onChange={handleChange}
            />
            <textarea
              name="description"
              placeholder="Description (optional)"
              rows={3}
              value={form.description}
              onChange={handleChange}
            />
            <button type="submit">Create Plan</button>
          </form>
        </div>

        {/* Plans List */}
        <div className="plans-list-card">
          <div className="plans-list-header">
            <h3>Existing Plans</h3>
            {loading && <span className="plans-loading">Loading...</span>}
          </div>

          {plans.length === 0 && !loading && (
            <p className="no-plans">No plans created yet.</p>
          )}

          <table className="plans-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Price (₹)</th>
                <th>Duration</th>
                <th>Description</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {plans.map((plan) => (
                <tr key={plan._id}>
                  <td>{plan.name}</td>
                  <td>{plan.price}</td>
                  <td>{plan.durationInDays} days</td>
                  <td>{plan.description || "-"}</td>
                  <td>
                    <button
                      className="delete-btn"
                      onClick={() => handleDelete(plan._id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

        </div>
      </div>
    </div>
  );
};

export default Plans;
