import { useEffect, useState } from "react";
import "./MyMembership.css";
import api from "../../services/api";

const MyMembership = () => {
  const [membership, setMembership] = useState(null);
  const [loading, setLoading] = useState(false);

  const loadMyMembership = async () => {
    try {
      setLoading(true);
      const res = await api.get("/api/memberships/my");
      setMembership(res.data.data);
    } catch (err) {
      alert("No active membership found");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadMyMembership();
  }, []);

  if (loading) return <p className="fq-loading">Loading...</p>;

  if (!membership)
    return <p className="fq-empty">You don’t have any active membership.</p>;

  return (
    <div className="fq-my-page">
      <h2>My Membership</h2>

      <div className="fq-card">
        <h3>{membership.plan.name}</h3>
        <p>Price: ₹ {membership.plan.price}</p>
        <p>Duration: {membership.plan.durationInDays} days</p>
        <p>Status: {membership.status.toUpperCase()}</p>
        <p>
          Expiry Date:{" "}
          {new Date(membership.endDate).toLocaleDateString()}
        </p>
      </div>
    </div>
  );
};

export default MyMembership;
