import { useEffect, useState } from "react";
import "./BuyMembership.css";
import { getActivePlans } from "../../services/planService";
import { buyMembership } from "../../services/membershipService";

const BuyMembership = () => {
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(false);
  const [buyingId, setBuyingId] = useState(null);

  const loadPlans = async () => {
    try {
      setLoading(true);
      const res = await getActivePlans();
      setPlans(res.data.data || []);
    } catch (err) {
      alert("Failed to load plans");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPlans();
  }, []);

  const handleBuy = async (planId) => {
    try {
      setBuyingId(planId);
      await buyMembership(planId);
      alert("✅ Membership purchased successfully!");
    } catch (err) {
      alert(err.response?.data?.message || "Failed to purchase membership");
    } finally {
      setBuyingId(null);
    }
  };

  return (
    <div className="fq-buy-page">
      <div className="fq-buy-header">
        <h2>Choose Your Membership</h2>
        <p>Select a plan and start your fitness journey with FitQuest</p>
      </div>

      {loading && <p className="fq-loading">Loading plans...</p>}

      <div className="fq-buy-grid">
        {plans.map((plan) => (
          <div key={plan._id} className="fq-buy-card">
            <h3>{plan.name}</h3>
            <p className="fq-buy-price">₹ {plan.price}</p>
            <p className="fq-buy-duration">
              {plan.durationInDays} Days Access
            </p>
            <p className="fq-buy-desc">{plan.description || "-"}</p>

            <button
              className="fq-primary-btn"
              onClick={() => handleBuy(plan._id)}
              disabled={buyingId === plan._id}
            >
              {buyingId === plan._id ? "Processing..." : "Buy Now"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BuyMembership;
