import { useEffect, useState } from "react";
import {
  getAllTrainers,
  createTrainer,
  deleteTrainer,
} from "../../services/trainerService";
import "./Trainers.css";
console.log("Trainers from API:", trainers)

const Trainers = () => {
  const [trainers, setTrainers] = useState([]);
  const [loading, setLoading] = useState(true);

  const [form, setForm] = useState({
    name: "",
    specialization: "",
    experience: "",
    phone: "",
  });

  const role = localStorage.getItem("role");

  // ✅ LOAD TRAINERS (ADMIN + MEMBER)
  const loadTrainers = async () => {
    try {
      const res = await getAllTrainers();
      setTrainers(res.data.data || []);
    } catch (err) {
      console.error("Failed to load trainers:", err);
      setTrainers([]); // prevents blank page
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadTrainers();
  }, []);

  // ✅ ADMIN CREATE
  const handleSubmit = async (e) => {
    e.preventDefault();
    await createTrainer(form);
    setForm({ name: "", specialization: "", experience: "", phone: "" });
    loadTrainers();
  };

  // ✅ ADMIN DELETE
  const handleDelete = async (id) => {
    await deleteTrainer(id);
    loadTrainers();
  };

  if (loading) return <p className="fq-loading">Loading trainers...</p>;

  return (
    <div className="fq-trainers-root">
      <h2>Gym Trainers</h2>

      {/* ✅ ADMIN ONLY FORM */}
      {role === "admin" && (
        <form className="fq-trainer-form" onSubmit={handleSubmit}>
          <input
            placeholder="Name"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
          />
          <input
            placeholder="Specialization"
            value={form.specialization}
            onChange={(e) =>
              setForm({ ...form, specialization: e.target.value })
            }
            required
          />
          <input
            placeholder="Experience (Years)"
            type="number"
            value={form.experience}
            onChange={(e) =>
              setForm({ ...form, experience: e.target.value })
            }
            required
          />
          <input
            placeholder="Phone"
            value={form.phone}
            onChange={(e) => setForm({ ...form, phone: e.target.value })}
            required
          />
          <button>Add Trainer</button>
        </form>
      )}

      {/* ✅ TRAINER LIST FOR BOTH ADMIN & MEMBER */}
      <div className="fq-trainer-grid">
        {trainers.length === 0 && <p>No trainers available</p>}

        {trainers.map((t) => (
          <div key={t._id} className="fq-trainer-card">
            <h4>{t.name}</h4>
            <p><strong>Specialization:</strong> {t.specialization}</p>
            <p><strong>Experience:</strong> {t.experience} yrs</p>
            <p><strong>Phone:</strong> {t.phone}</p>

            {/* ✅ ADMIN ONLY DELETE */}
            {role === "admin" && (
              <button onClick={() => handleDelete(t._id)}>Delete</button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Trainers;
