import { useEffect, useState } from "react";
import { getAllClasses, enrollClass } from "../../services/classService";
import "./Classes.css";

const MemberClasses = () => {
  const [classes, setClasses] = useState([]);

  const loadClasses = async () => {
    const res = await getAllClasses();
    setClasses(res.data.data);
  };

  useEffect(() => {
    loadClasses();
  }, []);

  const handleEnroll = async (id) => {
    try {
      await enrollClass(id);
      alert("Enrolled Successfully");
      loadClasses();
    } catch (err) {
      alert(err.response?.data?.message || "Enrollment failed");
    }
  };

  return (
    <div className="fq-class-root">
      <h2 className="fq-title">Available Classes</h2>

      <div className="fq-class-grid">
        {classes.map((c) => (
          <div className="fq-class-card" key={c._id}>
            <h4>{c.name}</h4>
            <p>Date: {new Date(c.date).toLocaleDateString()}</p>
            <p>Time: {c.time}</p>
            <p>Capacity: {c.capacity}</p>
            <p>Enrolled: {c.enrolledMembers?.length || 0}</p>

            <button onClick={() => handleEnroll(c._id)}>
              Enroll Now
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MemberClasses;
