import { useEffect, useState } from "react";
import {
  getAllTrainers,
  createTrainer,
  deleteTrainer,
} from "../../services/trainerService";
import "./Trainers.css";

const Trainers = () => {
  const [trainers, setTrainers] = useState([]);
  const [loading, setLoading] = useState(true);

  const [form, setForm] = useState({
    name: "",
    specialization: "",
    experience: "",
    phone: "",
  });

  const role = localStorage.getItem("role");

  const loadTrainers = async () => {
    try {
      const res = await getAllTrainers();
      setTrainers(res.data.data || []);
    } catch (err) {
      console.error("Failed to load trainers:", err);
      setTrainers([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadTrainers();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createTrainer(form);
      setForm({ name: "", specialization: "", experience: "", phone: "" });
      loadTrainers();
    } catch (err) {
      alert("Failed to add trainer");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this trainer?")) return;
    await deleteTrainer(id);
    loadTrainers();
  };

  if (loading) {
    return <p style={{ color: "#fff", padding: "30px" }}>Loading trainers...</p>;
  }

  return (
    <div className="fq-trainers-root">
      <h2>Gym Trainers</h2>

      {/* ✅ ADMIN ONLY FORM */}
      {role === "admin" && (
        <form className="fq-trainer-form" onSubmit={handleSubmit}>
          <input
            placeholder="Name"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
          />
          <input
            placeholder="Specialization"
            value={form.specialization}
            onChange={(e) =>
              setForm({ ...form, specialization: e.target.value })
            }
            required
          />
          <input
            placeholder="Experience (Years)"
            type="number"
            value={form.experience}
            onChange={(e) =>
              setForm({ ...form, experience: e.target.value })
            }
            required
          />
          <input
            placeholder="Phone"
            value={form.phone}
            onChange={(e) => setForm({ ...form, phone: e.target.value })}
            required
          />
          <button>Add Trainer</button>
        </form>
      )}

      {/* ✅ TRAINER LIST */}
      <div className="fq-trainer-grid">
        {trainers.length === 0 ? (
          <p style={{ color: "#ccc" }}>No trainers found.</p>
        ) : (
          trainers.map((t) => (
            <div key={t._id} className="fq-trainer-card">
              {/* ✅ NAME WILL ALWAYS RENDER NOW */}
              <h4>{t.name}</h4>

              <p>
                <strong>Specialization:</strong> {t.specialization}
              </p>
              <p>
                <strong>Experience:</strong> {t.experience} years
              </p>
              <p>
                <strong>Phone:</strong> {t.phone}
              </p>

              {role === "admin" && (
                <button onClick={() => handleDelete(t._id)}>Delete</button>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Trainers;
