import { useEffect, useState } from "react";
import { getAllTrainers, createTrainer, deleteTrainer } from "../../services/trainerService";
import "./Trainers.css";

const Trainers = () => {
  const [trainers, setTrainers] = useState([]);
  const [form, setForm] = useState({
    name: "",
    specialization: "",
    experience: "",
    phone: "",
  });

  const role = localStorage.getItem("role");

  const loadTrainers = async () => {
    const res = await getAllTrainers();
    setTrainers(res.data.data);
  };

  useEffect(() => {
    loadTrainers();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await createTrainer(form);
    setForm({ name: "", specialization: "", experience: "", phone: "" });
    loadTrainers();
  };

  const handleDelete = async (id) => {
    await deleteTrainer(id);
    loadTrainers();
  };

  return (
    <div className="fq-trainers-root">
      <h2>Gym Trainers</h2>

      {role === "admin" && (
        <form className="fq-trainer-form" onSubmit={handleSubmit}>
          <input placeholder="Name" onChange={(e) => setForm({ ...form, name: e.target.value })} required />
          <input placeholder="Specialization" onChange={(e) => setForm({ ...form, specialization: e.target.value })} required />
          <input placeholder="Experience (Years)" type="number" onChange={(e) => setForm({ ...form, experience: e.target.value })} required />
          <input placeholder="Phone" onChange={(e) => setForm({ ...form, phone: e.target.value })} required />
          <button>Add Trainer</button>
        </form>
      )}

      <div className="fq-trainer-grid">
        {trainers.map((t) => (
          <div key={t._id} className="fq-trainer-card">
            <h4>{t.name}</h4>
            <p>{t.specialization}</p>
            <p>{t.experience} yrs</p>
            <p>{t.phone}</p>

            {role === "admin" && (
              <button onClick={() => handleDelete(t._id)}>Delete</button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Trainers;
