import { useEffect, useState } from "react";
import {
  getAllTrainers,
  createTrainer,
  deleteTrainer,
} from "../../services/trainerService";
import "./Trainers.css";

const Trainers = () => {
  const [trainers, setTrainers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({
    name: "",
    specialization: "",
    experience: "",
    phone: "",
  });

  const role = localStorage.getItem("role");

  /* ✅ SAFE LOAD TRAINERS */
  const loadTrainers = async () => {
    try {
      const res = await getAllTrainers();
      setTrainers(res.data.data || []);
    } catch (err) {
      console.error("Failed to load trainers:", err);
      setTrainers([]); // ✅ prevents blank page crash
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadTrainers();
  }, []);

  /* ✅ ADD TRAINER (ADMIN ONLY) */
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createTrainer(form);
      setForm({
        name: "",
        specialization: "",
        experience: "",
        phone: "",
      });
      loadTrainers();
    } catch (err) {
      alert("Failed to add trainer");
    }
  };

  /* ✅ DELETE TRAINER (ADMIN ONLY) */
  const handleDelete = async (id) => {
    if (!window.confirm("Delete this trainer?")) return;
    try {
      await deleteTrainer(id);
      loadTrainers();
    } catch (err) {
      alert("Failed to delete trainer");
    }
  };

  return (
    <div className="fq-trainers-root">
      <h2>Gym Trainers</h2>

      {/* ✅ ADMIN CREATE FORM */}
      {role === "admin" && (
        <form className="fq-trainer-form" onSubmit={handleSubmit}>
          <input
            placeholder="Name"
            value={form.name}
            onChange={(e) =>
              setForm({ ...form, name: e.target.value })
            }
            required
          />
          <input
            placeholder="Specialization"
            value={form.specialization}
            onChange={(e) =>
              setForm({ ...form, specialization: e.target.value })
            }
            required
          />
          <input
            placeholder="Experience (Years)"
            type="number"
            value={form.experience}
            onChange={(e) =>
              setForm({ ...form, experience: e.target.value })
            }
            required
          />
          <input
            placeholder="Phone"
            value={form.phone}
            onChange={(e) =>
              setForm({ ...form, phone: e.target.value })
            }
            required
          />
          <button>Add Trainer</button>
        </form>
      )}

      {/* ✅ LOADING STATE */}
      {loading && <p>Loading trainers...</p>}

      {/* ✅ TRAINER GRID */}
      {!loading && trainers.length === 0 && (
        <p>No trainers available</p>
      )}

      <div className="fq-trainer-grid">
        {trainers.map((t) => (
          <div key={t._id} className="fq-trainer-card">
            <h4>{t.name}</h4>
            <p>{t.specialization}</p>
            <p>{t.experience} yrs</p>
            <p>{t.phone}</p>

            {/* ✅ ADMIN DELETE BUTTON */}
            {role === "admin" && (
              <button onClick={() => handleDelete(t._id)}>
                Delete
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Trainers;
